"""Utilities for embedding assets."""
import base64
from pathlib import Path
from typing import Optional, Union


def get_image_as_data_uri(image_path: Union[str, Path]) -> Optional[str]:
    """Convert image file to data URI.
    
    Args:
        image_path: Path to image file (local path or URL)
        
    Returns:
        Data URI string or None if image cannot be loaded
    """
    try:
        # Try local file first
        p = Path(image_path)
        if p.exists() and p.is_file():
            # Determine mime type from extension
            ext = p.suffix.lower()
            mime_types = {
                '.jpg': 'image/jpeg',
                '.jpeg': 'image/jpeg',
                '.png': 'image/png',
                '.gif': 'image/gif',
                '.webp': 'image/webp',
                '.svg': 'image/svg+xml'
            }
            mime_type = mime_types.get(ext, 'image/jpeg')
            
            with open(p, 'rb') as f:
                img_data = base64.b64encode(f.read()).decode('utf-8')
            return f"data:{mime_type};base64,{img_data}"
    except Exception:
        pass
    
    # For URLs, we can't reliably convert them in a self-contained way
    # Return None to let the template handle it
    return None


def get_placeholder_avatar_svg() -> str:
    """Return a placeholder avatar SVG for when image is unavailable."""
    return '''<svg xmlns="http://www.w3.org/2000/svg" width="85" height="85" viewBox="0 0 85 85" fill="none">
    <circle cx="42.5" cy="42.5" r="42.5" fill="#56817a" opacity="0.1"/>
    <circle cx="42.5" cy="25" r="12" fill="#56817a"/>
    <path d="M 25 60 Q 25 45 42.5 45 Q 60 45 60 60 L 60 70 Q 60 75 55 75 L 30 75 Q 25 75 25 70 Z" fill="#56817a" opacity="0.3"/>
</svg>'''


def get_placeholder_avatar_data_uri() -> str:
    """Return the placeholder avatar encoded as a data URI."""
    svg = get_placeholder_avatar_svg().encode('utf-8')
    encoded = base64.b64encode(svg).decode('ascii')
    return f"data:image/svg+xml;base64,{encoded}"


