def get_icon_svg(icon_name: str) -> str:
    """Get SVG for a specific icon.
    
    Args:
        icon_name: Name of the icon (location, email, phone, experience, education, hobbies, summary, linkedin)
        
    Returns:
        SVG string
    """
    icons = get_svg_icons()
    return icons.get(icon_name, '')
